import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AddFilm = () => {
    const [newFilm, setNewFilm] = useState({
        title: '',
        director: '',
        durationMinutes: 0,
        watched: false,
        releaseDate: '',
        rating: 0.0,
        popularity: 0.0,
    });

    const navigate = useNavigate();

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewFilm((prevFilm) => ({
            ...prevFilm,
            [name]: value,
        }));
    };

    const addFilm = async (e) => {
        e.preventDefault();
        try {
            await axios.post('http://localhost:8080/films', newFilm);
            navigate('/'); // Po dodaniu filmu wracamy na stronę główną (FilmList)
        } catch (error) {
            console.error('Błąd podczas dodawania filmu', error);
        }
    };

    return (
        <div>
            <h2>Dodaj Film</h2>
            <form onSubmit={addFilm}>
                <label>
                    Tytuł:
                    <input
                        type="text"
                        name="title"
                        value={newFilm.title}
                        onChange={handleInputChange}
                        required
                    />
                </label>
                <br />
                <label>
                    Reżyser:
                    <input
                        type="text"
                        name="director"
                        value={newFilm.director}
                        onChange={handleInputChange}
                        required
                    />
                </label>
                <br />
                <label>
                    Czas trwania (minuty):
                    <input
                        type="number"
                        name="durationMinutes"
                        value={newFilm.durationMinutes}
                        onChange={handleInputChange}
                        required
                    />
                </label>
                <br />
                <label>
                    Obejrzany:
                    <input
                        type="checkbox"
                        name="watched"
                        checked={newFilm.watched}
                        onChange={(e) =>
                            setNewFilm((prev) => ({
                                ...prev,
                                watched: e.target.checked,
                            }))
                        }
                    />
                </label>
                <br />
                <label>
                    Data wydania:
                    <input
                        type="date"
                        name="releaseDate"
                        value={newFilm.releaseDate}
                        onChange={handleInputChange}
                        required
                    />
                </label>
                <br />
                <label>
                    Ocena:
                    <input
                        type="number"
                        name="rating"
                        value={newFilm.rating}
                        onChange={handleInputChange}
                        step="0.1"
                        min="0"
                        max="10"
                        required
                    />
                </label>
                <br />
                <label>
                    Popularność:
                    <input
                        type="number"
                        name="popularity"
                        value={newFilm.popularity}
                        onChange={handleInputChange}
                        step="0.1"
                        min="0"
                        required
                    />
                </label>
                <br />
                <button type="submit">Dodaj film</button>
            </form>
        </div>
    );
};

export default AddFilm;
