import React, { useState, useEffect } from 'react';
import axios from 'axios';

const FilmList = () => {
    const [films, setFilms] = useState([]);
    const [pageCount, setPageCount] = useState(0);
    const [currentPage, setCurrentPage] = useState(0);
    const [searchQuery, setSearchQuery] = useState('');

    const fetchFilms = async (page, query) => {
        try {
            const response = await axios.get('/films', {
                params: {
                    page: page + 1, // Backend expects 1-based index for pagination
                    search: query,
                },
            });

            console.log('Response data:', response.data); // Sprawdzamy strukturę odpowiedzi
            console.log('Films content:', response.data.content); // Zobacz, co jest w content

            if (response.data && response.data.content) {
                setFilms(response.data.content); // Ustawiamy dane filmów
                setPageCount(response.data.totalPages); // Ustawiamy liczbę stron
            } else {
                setFilms([]); // Jeśli nie ma filmów, ustawiamy pustą listę
            }
        } catch (error) {
            console.error('There was an error fetching films:', error);
        }
    };




    // Wywołaj fetchFilms za każdym razem, gdy zmieni się numer strony lub zapytanie
    useEffect(() => {
        fetchFilms(currentPage, searchQuery);
    }, [currentPage, searchQuery]);


    return (
        <div>
            <h1>Lista Filmów</h1>



            <ul>
                {films.length > 0 ? (
                    films.map((film) => (
                        <li key={film.id}>
                            <strong>{film.title}</strong> ({new Date(film.releaseDate).toLocaleDateString()})
                            <br />
                            Reżyser: {film.director}
                            <br />
                            Czas trwania: {film.durationMinutes} min
                            <br />
                            Ocena: {film.rating}
                            <br />
                            <strong>{film.watched ? 'Oglądany' : 'Nieoglądany'}</strong>
                        </li>
                    ))
                ) : (
                    <li>Brak filmów do wyświetlenia</li>
                )}
            </ul>


        </div>
    );
};

export default FilmList;
