import React, { useEffect, useState } from 'react';
import axios from 'axios';

const FilmList = () => {
    const [films, setFilms] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Funkcja do pobierania filmów z API
        const fetchFilms = async () => {
            try {
                const response = await axios.get('http://localhost:8080/films');
                setFilms(response.data.content); // Pamiętaj, że paginacja może zwrócić dane w "content"
                setLoading(false);
            } catch (error) {
                console.error('Błąd podczas pobierania filmów', error);
                setLoading(false);
            }
        };

        fetchFilms();
    }, []);

    if (loading) {
        return <div>Ładowanie...</div>;
    }

    return (
        <div>
            <h1>Lista Filmów</h1>
            <ul>
                {films.map((film) => (
                    <li key={film.id}>
                        <h3>{film.title}</h3>
                        <p>{film.description}</p>
                        {/* Dodaj inne informacje o filmie, jak np. rok wydania */}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default FilmList;
