// src/components/FilmForm.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const FilmForm = ({ film, onSave, isEditing }) => {
    const [formData, setFormData] = useState({
        name: '',
        releaseDate: '',
        genre: '',
    });

    useEffect(() => {
        if (isEditing && film) {
            setFormData({
                name: film.name,
                releaseDate: film.releaseDate,
                genre: film.genre,
            });
        }
    }, [isEditing, film]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (isEditing) {
            onSave(film.id, formData); // Update existing film
        } else {
            onSave(formData); // Add new film
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <div>
                <label>Name:</label>
                <input
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                />
            </div>
            <div>
                <label>Release Date:</label>
                <input
                    type="date"
                    name="releaseDate"
                    value={formData.releaseDate}
                    onChange={handleChange}
                />
            </div>
            <div>
                <label>Genre:</label>
                <input
                    type="text"
                    name="genre"
                    value={formData.genre}
                    onChange={handleChange}
                />
            </div>
            <button type="submit">{isEditing ? 'Save Changes' : 'Add Film'}</button>
        </form>
    );
};

export default FilmForm;
