import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ReactPaginate from 'react-paginate';

const FilmList = () => {
    const [films, setFilms] = useState([]);
    const [pageCount, setPageCount] = useState(0);
    const [currentPage, setCurrentPage] = useState(0);
    const [searchQuery, setSearchQuery] = useState('');

    // Funkcja do pobierania danych o filmach
    const fetchFilms = async (page, search) => {
        try {
            const response = await axios.get('http://localhost:8080/films', {
                params: {
                    page: page + 1,  // Backend oczekuje numeru strony zaczynającego się od 1
                    search: search,
                },
            });
            setFilms(response.data.content); // Zakładamy, że API zwraca paginowane dane z polem 'content'
            setPageCount(response.data.totalPages); // Ustawiamy liczbę stron
        } catch (error) {
            console.error('Błąd podczas pobierania danych:', error);
        }
    };

    // Wywołaj fetchFilms za każdym razem, gdy zmieni się numer strony lub zapytanie
    useEffect(() => {
        fetchFilms(currentPage, searchQuery);
    }, [currentPage, searchQuery]);

    // Funkcja do obsługi zmiany strony
    const handlePageChange = ({ selected }) => {
        setCurrentPage(selected);
    };

    // Funkcja do obsługi zmiany zapytania wyszukiwania
    const handleSearch = (event) => {
        setSearchQuery(event.target.value);
        setCurrentPage(0);  // Zresetuj stronę, gdy zmienia się zapytanie wyszukiwania
    };

    return (
        <div>
            <h1>Lista Filmów</h1>

            <input
                type="text"
                placeholder="Szukaj filmów..."
                value={searchQuery}
                onChange={handleSearch}
            />

            <ul>
                {films.length > 0 ? (
                    films.map((film) => (
                        <li key={film.id}>
                            <strong>{film.title}</strong> ({new Date(film.releaseDate).toLocaleDateString()})
                            <br />
                            Reżyser: {film.director}
                            <br />
                            Czas trwania: {film.durationMinutes} min
                            <br />
                            Ocena: {film.rating}
                            <br />
                            <strong>{film.watched ? 'Oglądany' : 'Nieoglądany'}</strong>
                        </li>
                    ))
                ) : (
                    <li>Brak filmów do wyświetlenia</li>
                )}
            </ul>

            {/* Stronicowanie */}
            <ReactPaginate
                previousLabel={"Poprzednia"}
                nextLabel={"Następna"}
                pageCount={pageCount}
                onPageChange={handlePageChange}
                containerClassName={"pagination"}
                activeClassName={"active"}
            />
        </div>
    );
};

export default FilmList;
