// src/components/FilmList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import FilmForm from './FilmForm';
import ReactPaginate from 'react-paginate';

const FilmList = () => {
    const [films, setFilms] = useState([]);
    const [editingFilm, setEditingFilm] = useState(null);
    const [pageCount, setPageCount] = useState(0);
    const [currentPage, setCurrentPage] = useState(0);
    const [searchQuery, setSearchQuery] = useState('');

    useEffect(() => {
        fetchFilms(currentPage, searchQuery);
    }, [currentPage, searchQuery]);

    const fetchFilms = async (page, query) => {
        try {
            const response = await axios.get(`http://localhost:8080/films`, {
                params: {
                    page: page + 1, // Backend expects 1-based index for pagination
                    search: query,
                },
            });
            setFilms(response.data.content);
            setPageCount(response.data.totalPages);
        } catch (error) {
            console.error('There was an error fetching films:', error);
        }
    };

    const handleEdit = (film) => {
        setEditingFilm(film);
    };

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:8080/films/${id}`);
            fetchFilms(currentPage, searchQuery); // Refresh list after delete
        } catch (error) {
            console.error('There was an error deleting the film:', error);
        }
    };

    const handleSaveFilm = async (id, formData) => {
        try {
            if (id) {
                // Update film
                await axios.put(`http://localhost:8080/films/${id}`, formData);
            } else {
                // Add new film
                await axios.post('http://localhost:8080/films', formData);
            }
            fetchFilms(currentPage, searchQuery);
            setEditingFilm(null); // Close form after saving
        } catch (error) {
            console.error('There was an error saving the film:', error);
        }
    };

    const handleSearch = (e) => {
        setSearchQuery(e.target.value);
        setCurrentPage(0); // Reset to first page on search
    };

    return (
        <div>
            <h1>Film Tracker</h1>

            <input
                type="text"
                placeholder="Search Films"
                value={searchQuery}
                onChange={handleSearch}
            />

            <FilmForm
                film={editingFilm}
                onSave={handleSaveFilm}
                isEditing={!!editingFilm}
            />

            <ul>
                {films.map((film) => (
                    <li key={film.id}>
                        {film.name} ({film.releaseDate}) - {film.genre}
                        <button onClick={() => handleEdit(film)}>Edit</button>
                        <button onClick={() => handleDelete(film.id)}>Delete</button>
                    </li>
                ))}
            </ul>

            <ReactPaginate
                previousLabel={"Previous"}
                nextLabel={"Next"}
                breakLabel={"..."}
                pageCount={pageCount}
                onPageChange={({ selected }) => setCurrentPage(selected)}
                containerClassName={"pagination"}
                activeClassName={"active"}
            />
        </div>
    );
};

export default FilmList;
