import React, { useEffect, useState } from 'react';
import axios from 'axios';

const FilmList = () => {
    const [films, setFilms] = useState([]);
    const [loading, setLoading] = useState(true);
    const [newFilm, setNewFilm] = useState({
        title: '',
        director: '',
        durationMinutes: 0,
        watched: false,
        releaseDate: '',
        rating: 0.0,
        popularity: 0.0,
    });

    const [formVisible, setFormVisible] = useState(false);

    // Funkcja pobierająca filmy z API
    useEffect(() => {
        const fetchFilms = async () => {
            try {
                const response = await axios.get('http://localhost:8080/films');
                setFilms(response.data.content);
                setLoading(false);
            } catch (error) {
                console.error('Błąd podczas pobierania filmów', error);
                setLoading(false);
            }
        };

        fetchFilms();
    }, []);

    // Funkcja dodająca nowy film
    const addFilm = async () => {
        try {
            const response = await axios.post('http://localhost:8080/films', newFilm);
            setFilms([...films, response.data]); // Dodajemy nowy film do listy
            setFormVisible(false); // Ukrywamy formularz po dodaniu
            resetForm(); // Resetujemy formularz
        } catch (error) {
            console.error('Błąd podczas dodawania filmu', error);
        }
    };

    // Funkcja do resetowania formularza
    const resetForm = () => {
        setNewFilm({
            title: '',
            director: '',
            durationMinutes: 0,
            watched: false,
            releaseDate: '',
            rating: 0.0,
            popularity: 0.0,
        });
    };

    // Funkcja do obsługi zmian w formularzu
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewFilm((prevFilm) => ({
            ...prevFilm,
            [name]: value,
        }));
    };

    if (loading) {
        return <div>Ładowanie...</div>;
    }

    return (
        <div>
            <h1>Lista Filmów</h1>
            <ul>
                {films.map((film) => (
                    <li key={film.id}>
                        <h3>{film.title}</h3>
                        <p>{film.director}</p>
                        <p>{film.durationMinutes} min</p>
                        <p>{film.releaseDate}</p>
                        <p>Rating: {film.rating}</p>
                        <p>Popularity: {film.popularity}</p>
                    </li>
                ))}
            </ul>

            <button onClick={() => setFormVisible(true)}>Dodaj nowy film</button>

            {formVisible && (
                <div>
                    <h2>Dodaj Film</h2>
                    <form
                        onSubmit={(e) => {
                            e.preventDefault();
                            addFilm();
                        }}
                    >
                        <label>
                            Tytuł:
                            <input
                                type="text"
                                name="title"
                                value={newFilm.title}
                                onChange={handleInputChange}
                                required
                            />
                        </label>
                        <br />
                        <label>
                            Reżyser:
                            <input
                                type="text"
                                name="director"
                                value={newFilm.director}
                                onChange={handleInputChange}
                                required
                            />
                        </label>
                        <br />
                        <label>
                            Czas trwania (minuty):
                            <input
                                type="number"
                                name="durationMinutes"
                                value={newFilm.durationMinutes}
                                onChange={handleInputChange}
                                required
                            />
                        </label>
                        <br />
                        <label>
                            Obejrzany:
                            <input
                                type="checkbox"
                                name="watched"
                                checked={newFilm.watched}
                                onChange={(e) =>
                                    setNewFilm((prev) => ({
                                        ...prev,
                                        watched: e.target.checked,
                                    }))
                                }
                            />
                        </label>
                        <br />
                        <label>
                            Data wydania:
                            <input
                                type="date"
                                name="releaseDate"
                                value={newFilm.releaseDate}
                                onChange={handleInputChange}
                                required
                            />
                        </label>
                        <br />
                        <label>
                            Ocena:
                            <input
                                type="number"
                                name="rating"
                                value={newFilm.rating}
                                onChange={handleInputChange}
                                step="0.1"
                                min="0"
                                max="10"
                                required
                            />
                        </label>
                        <br />
                        <label>
                            Popularność:
                            <input
                                type="number"
                                name="popularity"
                                value={newFilm.popularity}
                                onChange={handleInputChange}
                                step="0.1"
                                min="0"
                                required
                            />
                        </label>
                        <br />
                        <button type="submit">Dodaj film</button>
                    </form>
                    <button onClick={() => setFormVisible(false)}>Anuluj</button>
                </div>
            )}
        </div>
    );
};

export default FilmList;
