import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const FilmList = () => {
    const [films, setFilms] = useState([]);
    const [loading, setLoading] = useState(true);
    const [searchTerm, setSearchTerm] = useState('');
    const [sortBy, setSortBy] = useState('title');
    const [sortOrder, setSortOrder] = useState('asc');  // domyślnie rosnąco

    const fetchFilms = async (search = '', sortBy = 'title', sortOrder = 'asc') => {
        setLoading(true);
        try {
            const response = await axios.get('http://localhost:8080/films', {
                params: {
                    search: search,
                    sortBy: sortBy,
                    sortOrder: sortOrder,
                },
            });
            setFilms(response.data.content);
        } catch (error) {
            console.error('Błąd podczas pobierania filmów', error);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchFilms(searchTerm, sortBy, sortOrder);
    }, [searchTerm, sortBy, sortOrder]);

    const handleSearchChange = (e) => {
        const value = e.target.value;
        setSearchTerm(value);
        fetchFilms(value, sortBy, sortOrder);
    };

    const handleSortByChange = (e) => {
        const value = e.target.value;
        setSortBy(value);
        fetchFilms(searchTerm, value, sortOrder);
    };

    const handleSortOrderChange = (e) => {
        const value = e.target.value;
        setSortOrder(value);
        fetchFilms(searchTerm, sortBy, value);
    };

    return (
        <div>
            <h1>Moja Lista Filmów</h1>

            <div style={{ marginBottom: '20px' }}>
                <input
                    type="text"
                    placeholder="Szukaj po tytule..."
                    value={searchTerm}
                    onChange={handleSearchChange}
                    style={{ padding: '8px', width: '300px', marginRight: '10px' }}
                />

                <select value={sortBy} onChange={handleSortByChange} style={{ padding: '8px', marginRight: '10px' }}>
                    <option value="title">Tytuł</option>
                    <option value="director">Reżyser</option>
                    <option value="releaseDate">Data wydania</option>
                    <option value="rating">Ocena</option>
                    <option value="popularity">Popularność</option>
                    <option value="durationMinutes">Czas trwania</option>
                </select>

                <select value={sortOrder} onChange={handleSortOrderChange} style={{ padding: '8px' }}>
                    <option value="asc">Rosnąco</option>
                    <option value="desc">Malejąco</option>
                </select>
            </div>

            {loading ? (
                <div>Ładowanie...</div>
            ) : (
                <ul>
                    {films.map((film) => (
                        <li key={film.id}>
                            <h3>{film.title}</h3>
                            <p>{film.director}</p>
                            <p>{film.durationMinutes} min</p>
                            <p>{film.releaseDate}</p>
                            <p>Rating: {film.rating}</p>
                            <p>Popularity: {film.popularity}</p>
                            <Link to={`/edit/${film.id}`}>Edytuj</Link>
                        </li>
                    ))}
                </ul>
            )}

            <Link to="/add">Dodaj nowy film</Link>
        </div>
    );
};

export default FilmList;
