import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const FilmList = () => {
    const [films, setFilms] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchFilms = async () => {
            try {
                const response = await axios.get('http://localhost:8080/films');
                setFilms(response.data.content);
                setLoading(false);
            } catch (error) {
                console.error('Błąd podczas pobierania filmów', error);
                setLoading(false);
            }
        };

        fetchFilms();
    }, []);

    if (loading) {
        return <div>Ładowanie...</div>;
    }

    return (
        <div>
            <h1>Lista Filmów</h1>
            <ul>
                {films.map((film) => (
                    <li key={film.id}>
                        <h3>{film.title}</h3>
                        <p>{film.director}</p>
                        <p>{film.durationMinutes} min</p>
                        <p>{film.releaseDate}</p>
                        <p>Rating: {film.rating}</p>
                        <p>Popularity: {film.popularity}</p>
                        <Link to={`/edit/${film.id}`}>Edytuj</Link>
                    </li>
                ))}
            </ul>
            <Link to="/add">
                <button>Dodaj nowy film</button>
            </Link>
        </div>
    );
};

export default FilmList;
