import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const FilmList = () => {
    const [films, setFilms] = useState([]);
    const [loading, setLoading] = useState(true);
    const [searchTerm, setSearchTerm] = useState('');

    const fetchFilms = async (search = '') => {
        setLoading(true);
        try {
            const response = await axios.get('http://localhost:8080/films', {
                params: {
                    search: search,
                },
            });
            setFilms(response.data.content);
        } catch (error) {
            console.error('Błąd podczas pobierania filmów', error);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchFilms();
    }, []);

    const handleSearchChange = (e) => {
        const value = e.target.value;
        setSearchTerm(value);
        fetchFilms(value);
    };

    return (
        <div>
            <h1>Moja Lista Filmów</h1>

            <input
                type="text"
                placeholder="Szukaj po tytule..."
                value={searchTerm}
                onChange={handleSearchChange}
                style={{ marginBottom: '20px', padding: '8px', width: '300px' }}
            />

            {loading ? (
                <div>Ładowanie...</div>
            ) : (
                <ul>
                    {films.map((film) => (
                        <li key={film.id}>
                            <h3>{film.title}</h3>
                            <p>{film.director}</p>
                            <p>{film.durationMinutes} min</p>
                            <p>{film.releaseDate}</p>
                            <p>Rating: {film.rating}</p>
                            <p>Popularity: {film.popularity}</p>
                            <Link to={`/edit/${film.id}`}>Edytuj</Link>
                        </li>
                    ))}
                </ul>
            )}

            <Link to="/add">Dodaj nowy film</Link>
        </div>
    );
};

export default FilmList;
