import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const FilmList = () => {
    const [films, setFilms] = useState([]);
    const [loading, setLoading] = useState(true);
    const [searchTerm, setSearchTerm] = useState('');
    const [sortBy, setSortBy] = useState('title');
    const [sortOrder, setSortOrder] = useState('asc');

    const fetchFilms = async (search = '', sortBy = 'title', sortOrder = 'asc') => {
        setLoading(true);
        try {
            const response = await axios.get('http://localhost:8080/films', {
                params: {
                    search: search,
                    sortBy: sortBy,
                    sortOrder: sortOrder,
                },
            });
            setFilms(response.data.content);
        } catch (error) {
            console.error('Błąd podczas pobierania filmów', error);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchFilms(searchTerm, sortBy, sortOrder);
    }, [searchTerm, sortBy, sortOrder]);

    const handleSearchChange = (e) => {
        const value = e.target.value;
        setSearchTerm(value);
        fetchFilms(value, sortBy, sortOrder);
    };

    const handleSortByChange = (e) => {
        const value = e.target.value;
        setSortBy(value);
        fetchFilms(searchTerm, value, sortOrder);
    };

    const handleSortOrderChange = (e) => {
        const value = e.target.value;
        setSortOrder(value);
        fetchFilms(searchTerm, sortBy, value);
    };

    return (
        <div style={styles.container}>
            <h1 style={styles.header}>Moja Lista Filmów</h1>

            <div style={styles.filtersContainer}>
                <input
                    type="text"
                    placeholder="Szukaj po tytule..."
                    value={searchTerm}
                    onChange={handleSearchChange}
                    style={styles.searchInput}
                />

                <select value={sortBy} onChange={handleSortByChange} style={styles.select}>
                    <option value="title">Tytuł</option>
                    <option value="director">Reżyser</option>
                    <option value="releaseDate">Data wydania</option>
                    <option value="rating">Ocena</option>
                    <option value="popularity">Popularność</option>
                    <option value="durationMinutes">Czas trwania</option>
                </select>

                <select value={sortOrder} onChange={handleSortOrderChange} style={styles.select}>
                    <option value="asc">Rosnąco</option>
                    <option value="desc">Malejąco</option>
                </select>
            </div>

            {loading ? (
                <div style={styles.loading}>Ładowanie...</div>
            ) : (
                <ul style={styles.list}>
                    {films.map((film) => (
                        <li key={film.id} style={styles.listItem}>
                            <h3 style={styles.title}>{film.title}</h3>
                            <p>{film.director}</p>
                            <p>{film.durationMinutes} min</p>
                            <p>{film.releaseDate}</p>
                            <p>Rating: {film.rating}</p>
                            <p>Popularity: {film.popularity}</p>
                            <Link to={`/edit/${film.id}`} style={styles.link}>Edytuj</Link>
                        </li>
                    ))}
                </ul>
            )}

            <Link to="/add" style={styles.addLink}>Dodaj nowy film</Link>
        </div>
    );
};

// Styles for the component
const styles = {
    container: {
        fontFamily: 'Helvetica Neue, Arial, sans-serif',
        backgroundColor: '#f5f5f5',  // Light background
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'flex-start',
        alignItems: 'center',
        padding: '30px',
        height: '100vh', // Full page height
        boxSizing: 'border-box',
        margin: '0',
    },
    header: {
        textAlign: 'center',
        color: '#1F1F1F',  // Dark text color
        marginBottom: '30px',
        fontSize: '2rem',
        fontWeight: 'bold',
    },
    filtersContainer: {
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        marginBottom: '30px',
        width: '100%',
    },
    searchInput: {
        padding: '12px',
        width: '100%',
        maxWidth: '400px',
        borderRadius: '8px',
        border: '1px solid #ddd',
        fontSize: '14px',
        marginBottom: '15px',
        backgroundColor: '#fff',
    },
    select: {
        padding: '12px',
        borderRadius: '8px',
        border: '1px solid #ddd',
        fontSize: '14px',
        width: '100%',
        maxWidth: '200px',
        marginBottom: '15px',
        backgroundColor: '#fff',
    },
    loading: {
        textAlign: 'center',
        fontSize: '20px',
        color: '#888',
    },
    list: {
        listStyleType: 'none',
        padding: '0',
        width: '100%',
        maxWidth: '800px',
    },
    listItem: {
        backgroundColor: '#ffffff',  // White background for list items
        padding: '15px',
        marginBottom: '15px',
        borderRadius: '8px',
        boxShadow: '0 2px 5px rgba(0, 0, 0, 0.1)',
    },
    title: {
        fontSize: '20px',
        fontWeight: 'bold',
        marginBottom: '5px',
        color: '#333',
    },
    link: {
        display: 'inline-block',
        marginTop: '15px',
        backgroundColor: '#007bff',  // Primary blue color
        color: '#fff',
        padding: '10px 20px',
        borderRadius: '25px',
        textDecoration: 'none',
        fontSize: '14px',
        textAlign: 'center',
    },
    addLink: {
        display: 'block',
        textAlign: 'center',
        marginTop: '30px',
        backgroundColor: '#28a745',  // Green for add button
        color: '#fff',
        padding: '12px 25px',
        borderRadius: '25px',
        textDecoration: 'none',
        fontSize: '16px',
    },

    // Media Queries for responsiveness
    '@media (max-width: 600px)': {
        container: {
            padding: '15px',
        },
        searchInput: {
            width: '100%',
            maxWidth: '100%',
        },
        select: {
            width: '100%',
            maxWidth: '100%',
        },
        filtersContainer: {
            flexDirection: 'column',
            alignItems: 'stretch',
        },
    },
};

export default FilmList;
