import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

const EditFilmForm = () => {
    const [film, setFilm] = useState(null);
    const navigate = useNavigate();
    const { id } = useParams();

    useEffect(() => {
        const fetchFilm = async () => {
            try {
                const response = await axios.get(`http://localhost:8080/films/${id}`);
                setFilm(response.data);
            } catch (error) {
                console.error('Błąd podczas pobierania filmu', error);
            }
        };
        fetchFilm();
    }, [id]);

    const handleInputChange = (e) => {
        const { name, value, type, checked } = e.target;

        // If the input is a date, ensure it is formatted correctly
        if (name === 'releaseDate' && type === 'date') {
            const formattedDate = new Date(value).toISOString().split('T')[0];  // This will format it to 'yyyy-MM-dd'
            setFilm((prevFilm) => ({
                ...prevFilm,
                [name]: formattedDate,
            }));
        } else {
            setFilm((prevFilm) => ({
                ...prevFilm,
                [name]: type === 'checkbox' ? checked : value,
            }));
        }
    };


    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.put(`http://localhost:8080/films/${id}`, film);
            navigate('/');
        } catch (error) {
            console.error('Błąd podczas zapisywania zmian', error);
        }
    };

    if (!film) return <div>Ładowanie...</div>;

    return (
        <div>
            <h2>Edytuj Film</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Tytuł:
                    <input
                        type="text"
                        name="title"
                        value={film.title || ''}
                        onChange={handleInputChange}
                        required
                    />
                </label>
                <br/>
                <label>
                    Reżyser:
                    <input
                        type="text"
                        name="director"
                        value={film.director || ''}
                        onChange={handleInputChange}
                        required
                    />
                </label>
                <br/>
                <label>
                    Czas trwania (minuty):
                    <input
                        type="number"
                        name="durationMinutes"
                        value={film.durationMinutes || 0}
                        onChange={handleInputChange}
                        required
                    />
                </label>
                <br/>
                <label>
                    Obejrzany:
                    <input
                        type="checkbox"
                        name="watched"
                        checked={film.watched || false}
                        onChange={handleInputChange}
                    />
                </label>
                <br/>
                <label>
                    Data wydania:
                    <input
                        type="date"
                        name="releaseDate"
                        value={film.releaseDate || ''}
                        onChange={handleInputChange}
                        required
                    />
                </label>
                <br/>
                <label>
                    Ocena:
                    <input
                        type="number"
                        name="rating"
                        value={film.rating || 0}
                        onChange={handleInputChange}
                        step="0.1"
                        min="0"
                        max="10"
                        required
                    />
                </label>
                <br/>
                <label>
                    Popularność:
                    <input
                        type="number"
                        name="popularity"
                        value={film.popularity || 0}
                        onChange={handleInputChange}
                        step="0.1"
                        min="0"
                        required
                    />
                </label>
                <br/>
                <button type="submit">Zapisz zmiany</button>

                <button
                    type="button"
                    onClick={async () => {
                        if (window.confirm('Czy na pewno chcesz usunąć ten film?')) {
                            try {
                                await axios.delete(`http://localhost:8080/films/${id}`);
                                navigate('/');
                            } catch (error) {
                                console.error('Błąd podczas usuwania filmu', error);
                            }
                        }
                    }}
                    style={{marginLeft: '10px', backgroundColor: 'red', color: 'white'}}
                >
                    Usuń film
                </button>
            </form>
        </div>
    );
};

export default EditFilmForm;
