import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

const EditFilmForm = () => {
    const [film, setFilm] = useState(null);
    const navigate = useNavigate();
    const { id } = useParams();

    useEffect(() => {
        const fetchFilm = async () => {
            try {
                const response = await axios.get(`http://localhost:8080/films/${id}`);
                setFilm(response.data);
            } catch (error) {
                console.error('Błąd podczas pobierania filmu', error);
            }
        };
        fetchFilm();
    }, [id]);

    const handleInputChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFilm((prevFilm) => ({
            ...prevFilm,
            [name]: type === 'checkbox' ? checked : value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.put(`http://localhost:8080/films/${id}`, film);
            navigate('/');
        } catch (error) {
            console.error('Błąd podczas zapisywania zmian', error);
        }
    };

    if (!film) return <div>Ładowanie...</div>;

    return (
        <div>
            <h2>Edytuj Film</h2>
            <form onSubmit={handleSubmit}>
                {/* Form fields are the same as in your original */}
                {/* ... */}
                <button type="submit">Zapisz zmiany</button>
            </form>
        </div>
    );
};

export default EditFilmForm;
