import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const FilmList = () => {
    const [films, setFilms] = useState([]);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();  // Hook navigate

    useEffect(() => {
        const fetchFilms = async () => {
            try {
                const response = await axios.get('http://localhost:8080/films');
                setFilms(response.data.content);
                setLoading(false);
            } catch (error) {
                console.error('Błąd podczas pobierania filmów', error);
                setLoading(false);
            }
        };

        fetchFilms();
    }, []);

    if (loading) {
        return <div>Ładowanie...</div>;
    }

    const handleEdit = (id) => {
        navigate(`/edit/${id}`);  // Używamy navigate do przejścia na stronę edycji
    };

    const handleAdd = () => {
        navigate('/add');  // Używamy navigate do przejścia na stronę dodawania filmu
    };

    return (
        <div>
            <h1>Lista Filmów</h1>
            <ul>
                {films.map((film) => (
                    <li key={film.id}>
                        <h3>{film.title}</h3>
                        <p>{film.director}</p>
                        <p>{film.durationMinutes} min</p>
                        <p>{film.releaseDate}</p>
                        <p>Rating: {film.rating}</p>
                        <p>Popularity: {film.popularity}</p>
                        <button onClick={() => handleEdit(film.id)}>Edytuj</button>
                    </li>
                ))}
            </ul>
            <button onClick={handleAdd}>Dodaj nowy film</button>
        </div>
    );
};

export default FilmList;
